class Solution {
 public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size(), dp[2]{}, ans = 0;
    for (int i = 0; i - k + 1 < n; i++) {
      if ((dp[colors[i % n]] = dp[!colors[i % n]] + 1) >= k) ans++;
      dp[!colors[i % n]] = 0;
    }
    return ans;
  }
};
