class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < n; ++ i) {
            colors.push_back(colors[i]);
        }
        int len = 1;
        int res = 0;
        for (int i = 1; i < n + k - 1; ++ i) {
            if (colors[i] != colors[i - 1]) {
                len ++; 
            } else {
                len = 1;
            }
            if(len == k) {
                res ++;
                len = k - 1;
            }
        }
        return res;


    }
};
