class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> cont_len(n, 0);
        int prev = colors[0];
        int alt_len = 1;
        for (int i = 1; i < n+k+3; i++) {
            if (prev != colors[i%n]) {
                alt_len++;
            } else {
                alt_len = 1;
            }
            cont_len[i%n] = alt_len;
            prev = colors[i%n];
        }
        int res = 0;
        for (int i = 0; i < n; i++) {
            if (cont_len[i] >= k) res++;
        }
        return res;
    }
};
