class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),ans=0;
        for(int i=0;i<k-1;i++)a.push_back(a[i]);
        for(int i=0;i<n;){
            int len=1;
            i++;
            while(i<a.size()&&a[i]!=a[i-1])i++,len++;
            ans+=max(0,len-k+1);
        }
        return ans;
    }
};
