class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {

        int i = 0 , j = 1 , n = nums.size() , pc = nums[0] , ans = 0;

        while(j<n+k-1){

            if(nums[j%n]!=pc){
                pc = nums[j%n];
            }else{
                i = j;
                pc = nums[j%n];
            }

            if(j-i+1==k){
                ans++;
                i++;
            }
            j++;
        }
        return ans;
    }
};
