#define inf 0x3f3f3f3f
#define Inf 0x3FFFFFFFFFFFFFFFLL
#define rep(i, n) for (int i = 0; i < (n); ++i)
#define Rep(i, n) for (int i = 1; i <= (n); ++i)
#define clr(x, a) memset(x, (a), sizeof x)
int const N = 200100;
int a[N], c[N][2];
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        Rep(i, n) a[i] = a[i + n] = colors[i - 1];
        Rep(i, n * 2) {
            if (i == 1) {
                c[i][0] = a[i] == 0;
                c[i][1] = a[i] == 1;
                continue;
            }
            c[i][0] = c[i - 2][0] + (a[i] == 0);
            c[i][1] = c[i - 2][1] + (a[i] == 1);
        }
        int ans = 0;
        for (int i = k; i <= n + k - 1; ++i) {
            int x = a[i];
            int y = a[i - 1];
            if (x == y) continue;
            if (k % 2 == 0) {
                int xs = c[i][x] - c[i - k][x];
                int ys = c[i - 1][y] - c[i - k + 1][y] + (a[i - k + 1] == y);
                if (xs + ys == k) ++ans;
            } else {
                int xs = c[i][x] - c[i - k + 1][x] + (a[i - k + 1] == x);
                int ys = c[i - 1][y] - c[i - k][y];
                if (xs + ys == k) ++ans;
            }
        }
        return ans;
    }
};
