class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> a(2*n),dp(2*n);
        for(int i=0;i<n;i++) a[i]=colors[i],a[i+n]=colors[i];
        dp[0]=1;
        for(int i=1;i<2*n;i++){
            if(a[i]==a[i-1]) dp[i]=1;
            else dp[i]=1+dp[i-1];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(dp[i+k-1]>=k) ans++;
        }
        return ans;
    }
};
