class Solution:
    def numberOfAlternatingGroups(self, nums: List[int], k: int) -> int:
        n = len(nums)
        nums = nums + nums
        n1 = len(nums)

        dp = [1] * n1
        for i in range(1, n1):
            if nums[i] != nums[i-1]:
                dp[i] += dp[i-1]

        ans = 0
        for i in range(n):
            if dp[i+k-1] >= k:
                ans += 1

        return ans
