class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        int ans = 0;
        for(int i = 0; i < n; ) 
        {
            int x = 1;
            while(i + 1 < n && colors[i + 1] != colors[i]) 
            {
                x++;
                i++;
            }
            if(x >= k) {
                ans += (x - k + 1);
            }
            else {
                i++;
            }
        }
        return ans;
    }
};
