class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0; i<k-1; i++){
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        int b = 0;
        int ans = 0;
        int next = 0;
        while(b <= n-k){
            while(next < n-1 && colors[next] != colors[next+1]){
                next++;
            }

            if(next - b >= k-1){
                ans += next-b-k+2;

            }

            b = next+1;
            next = b;

        }

        return ans;
    }
};
