class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int> pre(2 * n);
        for(int i = 1; i < pre.size(); ++i)
        {
            pre[i] = colors[i % n] == colors[(i - 1) % n];
        }

        for(int i = 1; i < pre.size(); ++i) {
            pre[i] += pre[i - 1];
        }

        int ans = 0;
        for(int i = 0; i < n; ++i)
        {
            int start = i;
            int end = i + k - 1;
            if(pre[end] - pre[start] == 0)
            {
                ++ans;
            }
        }

        return ans;
    }
};
