class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();
        vector<int> tem;
        for(int i = n - k + 1;i < n;i++)
        	tem.push_back(colors[i]);
       	for(auto x : colors) tem.push_back(x);
       	// for(int i = 0;i < k;i++) tem.push_back(colors[i]);

       	int pre = -1,num = 0;
       	for(auto c : tem){
       		if(pre != c){
       			pre = c;
       			num++;
       		}else{
       			num = 1;
       		}
       		if(num >= k) ans++;
       	}
    	return ans;
    }
};
