class Solution {
public:
    int dp[200010];
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        dp[0] = 1;
        int n = colors.size();
        int cnt = 0;
        for (int i = 1; i < 2 * n; ++i) {
            if (colors[i % n] != colors[(i - 1)%n]) dp[i] = dp[i - 1] + 1;
            else dp[i] = 1;
            if (i < n + k - 1) {
                cnt += dp[i] >= k;
              //  cout << i << " " << dp[i] << endl;
            }
        }
        return cnt;
    }
};
