class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

       for(int i = 0; i < k-1; i++) colors.push_back(colors[i]);

        int n = colors.size();

        vector<int>dp(n+1);
        dp[0] = 1;
        for(int i = 1; i < n; i++){
            if(colors[i] == colors[i - 1]) dp[i] = 1;
            else dp[i] = dp[i-1] + 1;
        }

        int ans =0;
        for(int i = 0; i < n; i++){
            if(dp[i] >= k)
            ans ++;
        }
        return ans;
    }
};
