class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors) 
        diff = [0]*n 
        for i in range(n): 
            if colors[i] != colors[(i + 1)%n]: 
                diff[i] = 1 
        k -= 1
        s = sum(diff[:k]) 
        ans = (s == k) 
        for i in range(n-1): 
            s -= diff[i] 
            s += diff[(i + k)%n]
            ans += (s == k) 
        return ans
