class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size();
    auto nxt = [&](int i) -> int {
      if (i == n - 1) return 0;
      return i + 1;
      };

    int ans = 0;
    vector<int> stk = { colors[0] };
    for (int i = 1; i < k - 1; i++) {
      if (colors[i] == stk.back()) stk.clear();
      stk.push_back(colors[i]);
    }

    for (int i = k - 1, cnt = 0; cnt < n; i = nxt(i), cnt++) {
      if (colors[i] == stk.back()) stk.clear();
      stk.push_back(colors[i]);
      if (stk.size() >= k) ans++;
    }
    return ans;
  }
};
