impl Solution {
        pub fn number_of_alternating_groups(colors: Vec<i32>, k: i32) -> i32 {
            let n = colors.len();
            let k = k as usize;
            let mut res = 0;
            let (mut i, mut j) = (0, 0);
            while i < n {
                let c = colors[i] as usize;
                while j < i + k {
                    let d = (j - i) % 2;
                    if (c + d + colors[j % n] as usize) % 2 > 0 {
                        break;
                    }
                    j += 1;
                }
                if j == i + k {
                    res += 1;
                }
                i += 1;
            }
            res
        }
}
