class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0 ; i<n; i++)colors.push_back(colors[i]);

        vector<int>sm(n+n,0);

        for(int i = 0; i<n+n-1; i++){
            if(colors[i]==colors[i+1])sm[i]=1;
        }
        int j = 0;
        int cnt = 0;
        while(j<k-1){
            cnt+=sm[j];
            j++;
        }
        j++;
        int ans =0 ;
        if(!cnt)ans++;
        for(int i = 1; i<n; i++, j++){
            cnt-=sm[i-1];
            cnt+=sm[j-1];
            if(!cnt)ans++;
        }return ans;
    }
};
