class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        arr = colors + colors[:(k - 1)]
        curr = 0
        ans = 0
        for i,x in enumerate(arr):
            if curr == 0 or x != arr[i - 1]:
                curr = min(curr + 1, k)
                if curr == k: ans += 1
            else:
                curr = 1
        return ans
