class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        cur = 0
        ans = 0
        n = len(colors)
        for i in range(n + k - 2):
            if colors[i % n] != colors[(i - 1) % n]:
                cur += 1
            else:
                cur = 0
            if cur >= k - 1:
                ans += 1
        return ans
