class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        vector<int> pre(n);
        for(int i=1;i<n-1;i++){
            if(colors[i] != colors[i-1] and colors[i] != colors[i+1]){
                pre[i] = 1;
            }
            pre[i] += pre[i-1];
        }
        // for(int i=0;i<n;i++){
        //     cout << pre[i] << " ";
        // }
        // cout << endl;
        int ans = 0;
        for(int i=0;i<=n-k;i++){
            // cout << i << " " <<  i+k-2 << endl;
            if(pre[i+k-2]-pre[i] == k-2){
                ans++;
            }
        }
        return ans;
    }
};
