class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if n < k:
            return 0
        def calc(colors):
            ans = 0
            j = 0
            i = 0
            while i < len(colors) - 1:
                if colors[i] == colors[i + 1]:
                    ans += max(0, i - j + 1 - k + 1)
                    i += 1
                    j = i
                else:
                    i += 1
            ans += max(0, i - j + 1 - k + 1)
            return ans
        return max(calc(colors[:] + colors[:k - 1]), calc(colors[n - k + 1:] + colors[:]))
