class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        vector<int> p(2*n+1, 0);
        for (int i=1; i<=2*n; i++) p[i] = p[i-1] + (a[(i-1)%n] != a[i%n]);

        int ans = 0;
        for (int i=1; i<=n; i++) ans += (p[i+k-1] - p[i] == k - 1);

        return ans;
    }
};
