class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        dp = [1] * n
        for i in range(1, n):
            if colors[i - 1] != colors[i]:
                dp[i] = dp[i - 1] + 1

        if colors[0] != colors[-1]:
            dp[0] = dp[-1] + 1

        for i in range(1, n):
            if colors[i - 1] != colors[i]:
                dp[i] = dp[i - 1] + 1

        return sum(e >= k for e in dp)
