func numberOfAlternatingGroups(colors []int, k int) int {
    n := len(colors)
    if n < k {
        return 0
    }

    calc := func(colors []int) int {
        ans := 0
        j := 0
        i := 0
        for i < len(colors)-1 {
            if colors[i] == colors[i+1] {
                if i-j+1 >= k {
                    ans += i - j + 1 - k + 1
                }
                i++
                j = i
            } else {
                i++
            }
        }
        if i-j+1 >= k {
            ans += i - j + 1 - k + 1
        }
        return ans
    }

    extendedColors1 := append(colors, colors[:k-1]...)
    extendedColors2 := append(colors[n-k+1:], colors...)
    return max(calc(extendedColors1), calc(extendedColors2))

}
