class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for(int i = 0; i < k - 1; ++i) {
            c.push_back(c[i]);
        }
        int n = c.size();
        int cr = c[0];
        int sz = 1;
        int ans = 0;
        for(int L = 1; L < n; ++L) {
            if(cr != c[L]) {
                sz++;
            } else {
                // 0 1 0 1 0
                ans += max(0, sz - k + 1);
                sz = 1;
            }
            cr = c[L];
        }
        ans += max(0, sz - k + 1);
        return ans;
    }
};
