class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n=col.size();
        for(int i=0;i<n;i++){
            col.push_back(col[i]);
        }
        int cnt=1;
        int ans=0;
        for(int i=1;i<n+k-1;i++){
            if(col[i]!=col[i-1]){
                cnt++;
            }
            else{
                if(cnt>=(k-1)){
                  //  cout<<cnt<<" ";
                    ans+=cnt-k+1;
                }
                cnt=1;
            }
        }
        if(cnt>=k-1){
            ans+=(cnt-k+1);
        }
        return ans;

    }
};
