class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        vector<int> b = {0, 0};
        b.insert(b.end(), a.begin(), a.end());
        b.insert(b.end(), a.begin(), a.end());
        for (int i = 2; i < b.size(); ++i) b[i] += b[i-2];
        const int n = a.size();

        int k2 = k / 2;
        int k1 = k - k2;
        int ans = 0;
        for (int i = 2; i < 2 + n; ++i) {
            int s1 = b[i + (k1 - 1) * 2] - b[i - 2];
            int s2 = b[i + 1 + (k2 - 1) * 2] - b[i - 1];
            if (s1 == 0 && s2 == k2) ++ans;
            else if(s1 == k1 && s2 == 0) ++ans;
        }
        return ans;
    }
};
