class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> v1(n,0),v2(n,0);
        for(int i=0;i<n;i++){
            if(colors[i]==(i%2)) v1[i]++;
            else v2[i]++;
            if(i!=0){
                v1[i]+=v1[i-1];
                v2[i]+=v2[i-1];
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if((i+k-1)<n){
                if(colors[i]==(i%2)){
                    int vl=v1[i+k-1];
                    if(i!=0) vl-=v1[i-1];
                    if(vl==k) ans++;
                }
                else{
                    int vl=v2[i+k-1];
                    if(i!=0) vl-=v2[i-1];
                    if(vl==k) ans++;
                }
            }
            else if(colors[n-1]!=colors[0]){
                if(colors[i]==(i%2)){
                    int vl=v1[n-1];
                    int ind=i+k-n-1;
                    if(colors[0]==0) vl+=v1[ind];
                    else vl+=v2[ind];
                    if(i!=0) vl-=v1[i-1];
                    if(vl==k) ans++;
                }
                else{
                    int vl=v2[n-1];
                    int ind=i+k-n-1;
                    if(colors[0]==0) vl+=v1[ind];
                    else vl+=v2[ind];
                    if(i!=0) vl-=v2[i-1];
                    if(vl==k) ans++;
                }
            }
            // cout<<i<<' '<<ans<<"\n";
        }
        return ans;
    }
};
