class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        int n = color.size();

        vector<int> temp = color;
        for(int i = 0; i < k-1; i++) {
            temp.push_back(color[i]);
        }

        n = temp.size();

        int i = 1;
        int cnt = 1;
        int res = 0;
        while(i < n) {
            if(temp[i] != temp[i-1]) {
                cnt++; 
                i++;
            } else {
                // cout<<i<<" "<<n<<endl;
                if(cnt >= k) {
                    res += (cnt-k+1);

                }
                cnt=1;
                i++; 
            }
        }
        // cout<<res<<" "<<cnt<<endl;
        if(cnt >= k) {
            res += (cnt-k+1);
            cnt = 1; 
        }

        return res;
    }
};
