class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v;
        v = c;
        for (int i : c) v.push_back(i);
        //滑动窗口
        int ans = 0, n = c.size();
        int l = 0;

        vector<bool> b(n, 0);
        for (int r = 1; r < v.size();) {
            //每次循环求以i结尾是否能构成交替组
            if (v[r] == v[r - 1]) {
                //不交替
                l = r;
                r++;
            }
            else {
                int pre = max(r - l - k + 2, 0);
                if (pre > 0) b[r % n] = 1;
                r++;
            }
        }
        for (bool bo: b) ans += bo;
        return ans;
    }
};
