class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> l(n);
        for(int i=0;i<n;i++){
            if(i==0)l[i]=1;
            else if(colors[i]!=colors[i-1])l[i]=l[i-1]+1;
            else l[i]=1;
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(i>=k-1){
                if(l[i]>=k)ans++;
            }
            else{
                if(i-l[i]+1!=0)continue;
                if(colors[0]==colors[n-1])continue;
                int len=k-l[i];
                if(l[n-1]>=len)ans++;
            }
        }
        return ans;
    }
};
