class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }

        int n = colors.size();
        int ans = 0, count = 1;
        for (int i = 1; i < n; i++) {
            if (colors[i] != colors[i - 1]) {
                count++;
            } else {
                ans += max(0, count - k + 1);
                count = 1;
            }
        }
                ans += max(0, count - k + 1);
        return ans;
    }
};
