class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors=colors+colors
        #print(colors)
        left=0
        res=0
        for i in range(1,n+k-1):
            if colors[i]==colors[i-1]:
                left=i 
            #(i,i-left+1)
            if i-left+1>=k:
                res+=1
                #print(i)
        return res
