#define ll long long
#define REP(i,n)        FOR(i,0,n)
#define SORT(v)         sort((v).begin(),(v).end())
#define FOR(i,a,b)      for(ll i=(a);i<(b);i++)

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        ll n = colors.size();
        vector<ll> suff(n, 1);
        for (ll i = n-2; i >= 0; i--) if (colors[i] != colors[i+1]) suff[i] += suff[i+1];
        ll res = 0;
        REP(i, n) {
            ll d = suff[i];
            if (colors[n-1] != colors[0] && d+i == n) d += suff[0];
            if (d >= k) res++;
        }
        return res;
    }
};
