class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        set<int> st;
        for(int i = 0 ; i < n ; ++i){
            if(i == 0){
                if(colors[n - 1] == colors[0]){
                    st.insert(i);
                }
            }
            else{
                if(colors[i - 1] == colors[i]){
                    st.insert(i);
                }
            }
        }
        if(st.empty()){
            return n;
        }
        int ans = 0;
        for(int i = 0 ; i < n ; ++i){
            auto nxt = st.upper_bound(i);
            if(nxt != st.end()){
                if((*nxt) - i >= k)++ans;
                continue;
            }
            int cur = n - i + min(i , *st.begin());
            if(cur >= k)++ans;
        }
        return ans;
    }
};
