class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        auto a = colors;
        for (auto i : colors) a.push_back(i);
        int n = colors.size();
        int ans = 0, st = 0;
        for (int i = 1; i < n + k - 1; i++) {
            if (a[i] == a[i-1]) st = i;
            ans += (i - st + 1) >= k;
        }
        return ans;
    }
};
