class Solution:
    def numberOfAlternatingGroups(self, A: List[int], k: int) -> int:
        B=[]
        n=len(A)
        for i in range(n):
            if A[i]!=A[i-1] and A[i]!=A[(i+1)%n]:
                B.append(1)
            else:
                B.append(0)
        cur=sum(B[:k-2])
        ans=0
        for i in range(n):
            cur+=B[(i+k-2)%n]-B[i]
            if cur==k-2:
                ans+=1
        return ans
