class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int ans = 0, temp = 0;
        for(int i = 0;i < k - 1;i++){
            if(v[i % n] != v[(i + 1) % n]) temp++;
        }
        if(temp == k - 1) ans++;
        for(int i = 1;i < n;i++){
            if(v[(i - 1) % n] != v[(i) % n]) temp--;
            if(v[(i + k - 2) % n] != v[(i + k - 1) % n]) temp++;
            if(temp == k - 1) ans++;
        }
        return ans;
    }
};
