/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(colors, k) {
    const arr = new Array(colors.length)
    const n = colors.length
    for(let i = 0;i<colors.length;i++){
        arr[i] = colors[i] !== colors[(i+1)%n]
    }
    let sum = 0
    let p1 = 0
    let p2 = 0
    while(p2 < k-1 ){
        if(arr[p2] === false){sum++}
        p2++
    }
    let res = 0
    while(p1 < n){
        if(sum === 0){res++}
        if(arr[p1] === false){sum--}
        p1++
        if(arr[p2] === false){sum++}
        p2 = (p2+1)%n
    }
    return res
};
