class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors *= 2
        m = n + k - 1
        # 枚举结束位置
        f = [0] * m
        f[0] = 1
        ans = 0
        for i in range(1, m):
            if colors[i] != colors[i-1]:
                f[i] = f[i-1] + 1
            else:
                f[i] = 1
        return sum(x >= k for x in f)
