class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> nums = colors;
        for(int i=0;i<n;i++){
            nums.push_back(colors[i]);
        }
        int str = 0;
        int ans = 0;
        for(int i=1;i<n+k-1;i++){
            if(nums[i] != nums[i-1]){
                if(i-str+1 == k){
                    ans++;
                    str++;
                }
                continue;
            }
            str = i;
        }
        return ans;
    }
};
