class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int ans=0;
        vector<int>b(n*2);
        for(int i=0;i<n;i++){
            b[i]=b[i+n]=a[i];
        }
        for(int i=0,j=0;i<n;i++){
            while(j<n*2&&(j==i||b[j]!=b[j-1]))j++;
            if(j-i>=k)ans++;
        }
        return ans;
    }
};
