class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> nums = colors;
        for(int i = 0; i<k-1; ++i){
            nums.push_back(colors[i]);
        }
        int n = nums.size();
        int ans = 0;
        int i = 0;
        for(int j = 1; j<n; ++j){
            if(nums[j]==nums[j-1]){
                i = j;
                continue;
            }
            int l = j-i+1;
            if(l == k){
                i++;
                ans++;
            }
        }
        return ans;
    }
};
