class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v=colors;
        int n=colors.size();

            for(int i=0;i<k-1;i++)
                v.push_back(colors[i]);


        n=v.size();

        vector<int> nv;

        for(int i=0;i<n-1;i++){
            if(v[i]!=v[i+1])
                nv.push_back(1);
            else nv.push_back(0);

        }
        nv.push_back(0);


        int i=0,j=0;
        k--;
        int ans=0;
        int sum=0;
        while(j<n){
            sum+=nv[j];
            if(j-i+1<k){
                j++;
            }
            else{
                if(sum==k)
                    ans++;
                sum-=nv[i];
                i++;
                j++;
            }
        }
        return ans;


    }
};
