class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors.extend(colors[:k - 1])
        n = len(colors)
        res = right = 0

        while right < n:
            left = right
            right += 1
            while right < n and colors[right - 1] != colors[right] and right - left + 1 <= k:
                right += 1
            right -= 1

            if right - left + 1 == k:
                res += 1
                while right + 1 < n and colors[right] != colors[right + 1]:
                    res += 1
                    right += 1

            right += 1

        return res
