class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size(),x=0;
        vector<int> ans(n);
        for(int i=0;i<n;i++){
            if(a[i]!=a[i-1>=0?i-1:n-1]) ans[i]=1;
            else x=1;
        }
        if(x==0) return n;
        else{
            vector<int> v;
            int x=0;
            for(int i=0;i<n;i++){
                if(ans[i]) x++;
                else{
                    if(x>0) v.push_back(x);
                    x=0;
                }
            }
            if(x>0) v.push_back(x);
            if(ans[n-1]==1 && ans[0]==1){
                v[0]+=v.back();
                v.pop_back();
            }
            int xxx=0;
            for(auto i:v){
                // cout<<i<<" ";
                if(i>=k-1) xxx+=i-(k-1)+1;
            }
            // cout<<endl;
            // for(auto i:ans) cout<<i<<" ";
            // cout<<endl;
            return xxx;
        }
        return 0;
    }
};
