class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt=0,pre=colors[0],n=colors.size(),res=0;
        for(int i=0;i<n+k-1;i++){
            if(pre!=colors[i%n])cnt++;
            else cnt=1;
            if(cnt>=k)res++;
            pre=colors[i%n];
        }
        return res;
    }
};
