class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] f = new int[n * 2];
        f[0] = 1;
        for (int i = 1; i < n * 2; i++) {
            if (colors[i % n] != colors[(i - 1 + n) % n]) {
                f[i] = f[i - 1] + 1;
            } else {
                f[i] = 1;
            }
        }
        int ans = 0;
        for (int i = n; i < n * 2; i++) {
            if (f[i] >= k) ans++;
        }
        return ans;
    }
}
