class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        diff = [colors[i] ^ colors[(i - 1) % n] for i in range(n)]

        ans = 0
        cnt = diff[:k - 1].count(0)
        for i, f in enumerate(diff):
            cnt -= f == 0
            cnt += diff[(i + k - 1) % n] == 0
            if cnt == 0:
                ans += 1
        return ans
